import React from "react";
import { useNavigate } from "react-router-dom";
import styles from "../../styles/styles";

const DropDown = ({ categoriesData, setDropDown }) => {
  const navigate = useNavigate();

  const submitHandle = (i) => {
    navigate(`/products?category=${i.title}`);
    setDropDown(false);
    window.location.reload();
  };

  return (
    <div
      className="pb-2 w-[200px] bg-[#fff] absolute z-30 rounded-md shadow-sm"
      style={{ border: "1px solid #ccc", borderRadius: "8px" }}
    >
      {categoriesData &&
        categoriesData.map((i, index) => (
          <div
            key={index}
            className={`${styles.noramlFlex} hover:bg-gray-100 cursor-pointer`}
            onClick={() => submitHandle(i)}
            style={{
              borderBottom: "1px solid #ccc",
              padding: "10px",
              transition: "background-color 0.3s ease",
              color: "black", // Màu chữ
            }}
          >
            <img
              src={i.image_Url}
              style={{
                width: "15px", // Giảm kích thước ảnh
                height: "15px",
                objectFit: "contain",
                marginLeft: "5px",
                userSelect: "none",
              }}
              alt=""
            />
            <h3 className="m-3 cursor-pointer select-none text-sm">
              {i.title}
            </h3>
          </div>
        ))}
    </div>
  );
};

export default DropDown;
