import React, { useState } from "react";
import styles from "../../styles/styles";
// import { Link } from "react-router-dom";
import { RxCross1 } from "react-icons/rx";
import { IoBagHandleOutline } from "react-icons/io5";
import { BsCartPlus } from "react-icons/bs";
// import { HiOutlineMinus, HiPlus } from "react-icons/hi";

const Wishlist = ({ setOpenWishlist }) => {
  const cartData = [
    {
      name: "Vi Sinh Obinic",
      description: "Test",
      price: "38000đ",
    },
    {
      name: "Vi Sinh Obinic",
      description: "Test",
      price: "38000đ",
    },
    {
      name: "Vi Sinh Obinic",
      description: "Test",
      price: "38000đ",
    },
  ];
  return (
    <div className="fixed top-0 left-0 w-full bg-[#0000004b] h-screen z-10">
      <div className="fixed top-0 right-0 min-h-full w-[25%] bg-white flex flex-col justify-between shadow-sm">
        <div>
          <div className="flex w-full justify-end pt-5 pr-5">
            <RxCross1
              size={25}
              className="cursor-pointer"
              onClick={() => setOpenWishlist(false)}
            />
          </div>
          {/**Tiêu đề */}
          <div className={`${styles.noramlFlex} p-4`}>
            <IoBagHandleOutline size={25} />
            <h5 className="pl-2 text-[15px] font-[500]">
              3 Sản phẩm yêu thích
            </h5>
          </div>
          {/**Danh sách sản phẩm */}
          <br />
          <div className="w-full border-t">
            {cartData &&
              cartData.map((i, index) => <CartSingle key={index} data={i} />)}
          </div>
        </div>
        {/* <div className="px-5 mb-3">
          <Link to="/checkout">
            <div
              className={`h-[45px] flex items-center justify-center w-[100%] bg-[#e44343] rounded-[5px]`}
            >
              <h1 className="text-[#fff] text-[18px] font-[600]">
                Thanh toán (500.000đ)
              </h1>
            </div>
          </Link>
        </div> */}
      </div>
    </div>
  );
};

const CartSingle = ({ data }) => {
  const [value, setValue] = useState(1);
  const totalPrice = (parseFloat(data.price) * value).toLocaleString("vi-VN", {
    style: "currency",
    currency: "VND",
    minimumFractionDigits: 0, // Set to the desired number of decimal places
    maximumFractionDigits: 0, // Set to the desired number of decimal places
  });

  return (
    <div className="border-b p-4">
      <div className="w-full 800px:flex items-center">
        <RxCross1 className="cursor-pointer 800px:mb-['unset'] 800px:ml-['unset'] mb-2 ml-2"
        // onClick={() => removeFromWishlistHandler(data)}
        />
        <img
          src={`${data?.images[0]?.url}`}
          alt=""
          className="w-[130px] h-min ml-2 mr-2 rounded-[5px]"
        />

        <div className="pl-[5px]">
          <h1>{data.name}</h1>
          <h4 className="font-[600] pt-3 800px:pt-[3px] text-[17px] text-[#d02222] font-Roboto">
            US${totalPrice}
          </h4>
        </div>
        <div>
          <BsCartPlus size={20} className="cursor-pointer" tile="Add to cart"
        //    onClick={() => addToCartHandler(data)}
          />
        </div>
      </div>
    </div>
  );
};
export default Wishlist;
